import React, { useState, Component } from 'react';
import Header from './Header.jsx'
import {
  NavLink,
  Link,
  withRouter
} from 'react-router-dom';
import './Nav.css';

class Nav extends Component {
  constructor(props) {
    super(props)
    this.state = {
      projectsOpen: false
    }

    console.log(props)
  }

  render() {
    console.log('PROPS:', this.props)
    let openAtPageLoad;

    let headerColor = this.props.data.titleColor;
    let linkColor = this.props.data.navlinkColor;
    let activeLinkColor = this.props.data.activeLinkColor;

    // handles sending proper text to header based on location
    let headerText = getPathName(this.props.location)
    // handles active state for queries on projects page
    let activeQuery = getQuery(this.props.location);
    // checks open at pageload
    this.props.location.pathname === '/projects' ? openAtPageLoad = true : openAtPageLoad = false;

    if (this.state.projectsOpen || openAtPageLoad) {
      return (
        <div className="header">
          <Header title={headerText} data={headerColor} />
          <nav>
            <ul className="nav">
              <li><Link activeClassName="active" to="/" onClick={() => this.setState({projectsOpen: false})}>home</Link></li>
              <li><Link activeClassName="active" to="/projects" onClick={() => this.setState({projectsOpen: true})}>projects</Link></li>
              <ul>
                <li><Link to="/projects?personal" className={activeQuery === '?personal' ? 'active' : 'inactive'}>personal</Link></li>
                <li><Link to="/projects?professional" className={activeQuery === '?professional' ? 'active' : 'inactive'}>professional</Link></li>
                <li><Link to='/projects?all' className={activeQuery === '?all' ? 'active' : 'inactive'}>all projects</Link></li>
              </ul>
              <li><Link activeClassName="active" to="/contact" onClick={() => this.setState({projectsOpen: false})}>contact</Link></li>
            </ul>
          </nav>
        </div>
      )
    } else {
      return (
        <div className="header">
          <Header title={headerText} data={headerColor} />
          <nav>
            <ul>
              <li><Link to="/" className='link' onClick={() => this.setState({projectsOpen: false})}>home</Link></li>
              <li><Link to="/projects" className='link' onClick={() => this.setState({projectsOpen: true})}>projects</Link></li>
              <li><Link to="/contact" className='link' onClick={() => this.setState({projectsOpen: false})}>contact</Link></li>
            </ul>
          </nav>
        </div>
      )
    }
  }
}

function getPathName(location) {
  let headerText;

  if (location.pathname === '/') {
    headerText = 'james ming johnson';
  } else if (location.pathname === '/projects') {
    if (location.search === '?professional') {
      headerText = 'projects: professional';
    } else if (location.search === '?personal') {
      headerText = 'projects: personal';
    } else {
      headerText = 'projects';
    }
  } else if (location.pathname === '/contact') {
    headerText = 'contact';
  }

  return headerText;
}

function getQuery(location) {
  return location.search;
}

export default withRouter(Nav);
