import React, { Component } from 'react';
import Data from '../json/projects.json';
import './Projects.css'

class Projects extends Component {
    constructor(props) {
        super(props)
        this.state = {
            personal: Data.personal,
            professional: Data.professional,
            all: Data.personal.concat(Data.professional)
        }
    }

    render() {
        let type = 'all';
        let query = this.props.location.search.substring(1);

        if (query === 'professional') {
            type = 'professional';
        } else if (query === 'personal') {
            type = 'personal';
        }

        return (
            <div className="projectsContainer">
                {this.state[type].map((project, i) => (
                    <div className="cardBody" key={i}>
                        <img src={project.img} alt="" className="thumbnail" />
                        <h3 className="title">{project.name}</h3>
                        <Description data={project}></Description>
                    </div>
                ))}
            </div>
        )
    }
}

function Description(project) {
    if (project.data.url) {
        return (
            <p>{project.data.desc} <a href="project.data.url">Visit.</a></p>
        )
    } else {
        return (
            <p>{project.data.desc}</p>
        )
    }
}

export default Projects;
