        }

        let currentViewSize = window.innerWidth;
        if (currentViewSize < 350) {
            styles.position = 'static';
        }

        return (
            <div className="footerDiv">
                <p className="footer" style={{ color: styles.color, marginBottom: "0px" }}>Â© 2020 James Johnson. Programmed in React.js and Node.js.
                Background: {this.props.data.artist}, {this.props.data.title}, {this.props.data.date} </p>
            </div>
        )
    }
}

export default Footer;
