import React, { useState } from 'react';
import Header from './Header.jsx'
import {
  NavLink,
  Link,
  useLocation
} from 'react-router-dom';
import './Nav.css';

function Nav(props) {
  const [projectsOpen, updateState] = useState(false)
  let location = useLocation();
  let openAtPageLoad;

  let headerColor = props.data.titleColor;
  let linkColor = props.data.navlinkColor;
  let activeLinkColor = props.data.activeLinkColor;

  //handles sending proper text to header based on location
  let headerText = getPathName(location)
  //handles active state for queries on projects page
  let activeQuery = getQuery(location);
  // checks open at pageload
  location.pathname === '/projects' ? openAtPageLoad = true : openAtPageLoad = false;

  if (projectsOpen || openAtPageLoad) {
    return (
      <div className="header">
        <Header title={headerText} data={headerColor} />
        <nav>
          <ul className="nav">
            <li><NavLink exact activeStyle={{color: 'green'}} activeClassName="active" to="/" onClick={() => updateState(false)}>home</NavLink></li>
            <li><NavLink exact activeClassName="active" to="/projects" activeStyle={{color: 'green'}} onClick={() => updateState(true)}>projects</NavLink></li>
            <ul>
              <li><Link to="/projects?personal" className={activeQuery === 'personal' ? 'active' : 'inactive'}>personal</Link></li>
              <li><Link to="/projects?professional" className={activeQuery === 'professional' ? 'active' : 'inactive'}>professional</Link></li>
              <li><Link to='/projects?all' className={activeQuery === 'all' ? 'active' : 'inactive'}>all projects</Link></li>
            </ul>
            <li><NavLink activeClassName="active" to="/contact" activeStyle={{color: 'green'}} onClick={() => updateState(false)}>contact</NavLink></li>
          </ul>
        </nav>
      </div>
    )
  } else {
    return (
      <div className="header">
        <Header title={headerText} data={headerColor} />
        <nav>
          <ul>
            <li><NavLink exact to="/" className='link' onClick={() => updateState(false)}>home</NavLink></li>
            <li><NavLink to="/projects" className='link' onClick={() => updateState(true)}>projects</NavLink></li>
            <li><NavLink to="/contact" className='link' onClick={() => updateState(false)}>contact</NavLink></li>
          </ul>
        </nav>
      </div>
    )
  }
}

function getPathName(location) {
  let headerText;

  if (location.pathname === '/') {
    headerText = 'james ming johnson';
  } else if (location.pathname === '/projects') {
    if (location.search === '?professional') {
      headerText = 'projects: professional';
    } else if (location.search === '?personal') {
      headerText = 'projects: personal';
    } else {
      headerText = 'projects';
    }
  } else if (location.pathname === '/contact') {
    headerText = 'contact';
  }

  return headerText;
}

function getQuery(location) {
  let query;
  if (location.search === '?personal') {
    query = 'personal'
  } else if (location.search === '?professional') {
    query = 'professional'
  } else if (location.search === '?all') {
    query = 'all'
  } else if (location.search === '' && location.pathname === '/projects') {
    query = 'all'
  } else {
    query = '';
  }

  return query;
}

export default Nav;
