import React from 'react';
import Header from './Header.jsx'
import {
  Link,
  useLocation
} from 'react-router-dom';

class Nav extends Component {
  render() {
    let location = useLocation().pathname;
    let headerText;

    if (location === '/') {
      headerText = 'james johnson';
    } else if (location === '/projects') {
      headerText = 'projects';
    } else if (location === '/contact') {
      headerText = 'contact';
    }

    return (
      <div className="header">
        <Header title={headerText} />
        <nav>
          <ul>
            <li><Link to="/">home</Link></li>
            <li><Link to="/projects" onClick={console.log('clicked projects')}>projects</Link></li>
              <ul>
                <li><Link to="/projects">personal</Link></li>
                <li><Link to="/projects">professional</Link></li>
              </ul>
            <li><Link to="/contact">contact</Link></li>
          </ul>
        </nav>
      </div>
    )
  }

}

export default Nav;
