import React, { useState } from 'react';
import Header from './Header.jsx'
import {
  NavLink,
  Link,
  useLocation
} from 'react-router-dom';
import './Nav.css';

function Nav(props) {
  const [projectsOpen, updateState] = useState(false)
  let headerColor = props.data.color;
  let location = useLocation();
  let headerText;

  getLocation(location)

  //handles sending proper text to header based on location
  if (location.pathname === '/') {
    headerText = 'james ming johnson';
  } else if (location.pathname === '/projects') {
    headerText = 'projects';
  } else if (location.pathname === '/contact') {
    headerText = 'contact';
  }

  //handles active state for queries on projects page
  if (location.search === '?personal') {
    console.log('on personal')
  } else if (location.search === '?professional') {
    console.log('on professional')
  } else if (location.search === '?all') {
    console.log('on all')
  } else if (location.search === '' && location.pathname === '/projects'){
    console.log('on all')
  } else {
    console.log('none')
  }

  if (projectsOpen) {
    return (
      <div className="header">
        <Header title={headerText} data={headerColor}/>
        <nav>
          <ul className="nav">
            <li><NavLink exact activeClassName="active" to="/" onClick={() => updateState(false)}>home</NavLink></li>
            <li><NavLink activeClassName="active" to="/projects" className='link' onClick={() => updateState(true)}>projects</NavLink></li>
            <ul>
              <li><Link to="/projects?personal" className='link'>personal</Link></li>
              <li><Link to="/projects?professional" className='link'>professional</Link></li>
              <li><Link to='/projects?all' className='link'>all projects</Link></li>
            </ul>
            <li><NavLink activeClassName="active" to="/contact" className='link' onClick={() => updateState(false)}>contact</NavLink></li>
          </ul>
        </nav>
      </div>
    )
  } else {
    return (
      <div className="header">
        <Header title={headerText} data={headerColor}/>
        <nav>
          <ul>
            <li><NavLink exact to="/" className='link' onClick={() => updateState(false)}>home</NavLink></li>
            <li><NavLink to="/projects" className='link' onClick={() => updateState(true)}>projects</NavLink></li>
            <li><NavLink to="/contact" className='link' onClick={() => updateState(false)}>contact</NavLink></li>
          </ul>
        </nav>
      </div>
    )
  }
}

