import React, { Component } from 'react';
import {
    BrowserRouter as Router,
} from 'react-router-dom';
import RoutedNav from './components/RoutedNav.jsx';
import './App.css';

// get background images to set and change homepage background
var URLs = ["/images/rubens.jpg", "/images/turner.jpg"]
var randomNum = Math.round(Math.random())

class App extends Component {
    render() {
        // control the background style of the home page
        var url = URLs[randomNum];
        var styles = {
            backgroundColor: "#023000",
            backgroundImage: "url(" + url + ")",
            backgroundSize: "cover",
            backgroundrepeat: "no-repeat",
            backgroundPosition: "center",
            backgroundAttachment: "fixed",
            width: "100vw",
            height: "100vh"
        }
        return (
            <div className="App" style={styles}>
                <Background>
                    <div className="mainModal">
                        <Router>
                            <RoutedNav />
                        </Router>
                    </div>
                </Background>
            </div>
        )
    }
}

export default App;
