import React, { Component } from 'react';
import {
    Switch,
    Route
} from 'react-router-dom';
import Nav from './components/Nav.jsx';
import Home from './components/Home.jsx';
import Projects from './components/Projects.jsx';
import Contact from './components/Contact.jsx'
import './App.css';

let backgrounds = ['/images/vandevelde.jpg', '/images/turner.jpg'];
let randomNum = Math.round(Math.random(0, 1))
let background = backgrounds[randomNum];

let styles = {
    backgroundImage: 'url(' + background + ')',
    backgroundRepeat: 'no-repeat',
    backgroundPosition: 'center',
    backgroundAttachment: 'fixed',
    height: '100vh',
    width: '100vw',
    verticalAlign: 'top',
    margin: '0'
}


class App extends Component {
    render() {
        return (
            <div className="App" style={styles}>
                <div className="mainContainer">
                    <div className="nav">
                        <Nav />
                    </div>
                    <Switch>
                        <Route exact path="/" component={Home} />
                        <Route path="/projects" component={Projects} />
                        <Route path="/contact" component={Contact} />
                    </Switch>
                </div>
                <Footer/>
            </div>
        )
    }
}

function Footer() {
    return (
        <p className="footer">programmed by james johnson in react.js and node.js</p>
    )
}

export default App;
