import React, { useState } from 'react';
import Header from './Header.jsx'
import {
  Link,
  useLocation
} from 'react-router-dom';

function Nav() {
  const [projectsOpen, updateState] = useState(false)

  let location = useLocation().pathname;
  let headerText;

  if (location === '/') {
    headerText = 'james johnson';
  } else if (location === '/projects') {
    headerText = 'projects';
  } else if (location === '/contact') {
    headerText = 'contact';
  }

  if (projectsOpen) {
    return (
      <div className="header">
        <Header title = {headerText} />
        <ExpandedNav />
      </div>
    )
  } else {
    return (
      <RegularNav />
    )
  }
}

function RegularNav() {
  return (
    <div className="header">
      <Header title={headerText} />
      <nav>
        <ul>
          <li><Link to="/" onClick={() => updateState(false)}>home</Link></li>
          <li><Link to="/projects" onClick={() => updateState(true)}>projects</Link></li>
          <li><Link to="/contact" onClick={() => updateState(false)}>contact</Link></li>
        </ul>
      </nav>
    </div>

  )
}

function ExpandedNav() {
  return (
    <div className="header">
      <Header title={headerText} />
      <nav>
        <ul>
          <li><Link to="/" onClick={() => updateState(false)}>home</Link></li>
          <li><Link to="/projects" onClick={() => updateState(true)}>projects</Link></li>
          <ul>
            <li><Link to="/projects?personal">personal</Link></li>
            <li><Link to="/projects?professional">professional</Link></li>
            <li><Link to='/projects?all'>all</Link></li>
          </ul>
          <li><Link to="/contact" onClick={() => updateState(false)}>contact</Link></li>
        </ul>
      </nav>
    </div>
  )
}

export default Nav;
